package main

import (
	"bufio"
	"encoding/json"
	"fmt"
	"log"
	"os"
)

func main() {
	// Open the JSON file
	file, err := os.Open("data.json")
	if err != nil {
		log.Fatalf("Error opening file: %v", err)
	}
	defer file.Close()

	// Create a scanner to read the file line-by-line
	scanner := bufio.NewScanner(file)

	// Create a slice to hold all JSON objects
	var allData []map[string]interface{}

	for scanner.Scan() {
		line := scanner.Text()

		// Declare a map to hold the unmarshalled JSON object
		var data map[string]interface{}

		// Unmarshal each line into a map
		if err := json.Unmarshal([]byte(line), &data); err != nil {
			log.Fatalf("Error unmarshalling JSON: %v", err)
		}

		// Append the unmarshalled map to the slice
		allData = append(allData, data)
	}

	// Check for errors during scanning
	if err := scanner.Err(); err != nil {
		log.Fatalf("Error reading file: %v", err)
	}

	// At this point, allData contains all JSON objects as a slice of maps

	// Example: marshal the complete slice back to a pretty-printed JSON for verification
	jsonOutput, err := json.MarshalIndent(allData, "", "  ")
	if err != nil {
		log.Fatalf("Error marshalling JSON: %v", err)
	}

	// Print the marshalled JSON
	fmt.Println(string(jsonOutput))
}
```

